#!/bin/bash

# Quick setup script for GitHub Actions deployment
# This script helps you set up the Google Cloud service account and outputs the secrets you need

set -e

echo "🚀 GitHub Actions Deployment Setup"
echo "===================================="
echo ""

# Check if gcloud is installed
if ! command -v gcloud &> /dev/null; then
    echo "❌ Error: gcloud CLI is not installed"
    echo "Install it from: https://cloud.google.com/sdk/docs/install"
    exit 1
fi

# Get project ID
read -p "Enter your Google Cloud Project ID: " PROJECT_ID
if [ -z "$PROJECT_ID" ]; then
    echo "❌ Project ID is required"
    exit 1
fi

# Set project
gcloud config set project $PROJECT_ID

# Service account name
SA_NAME="github-actions-sa"
SA_EMAIL="${SA_NAME}@${PROJECT_ID}.iam.gserviceaccount.com"

echo ""
echo "📋 Step 1: Enabling required APIs..."
gcloud services enable cloudbuild.googleapis.com --quiet
gcloud services enable run.googleapis.com --quiet
gcloud services enable containerregistry.googleapis.com --quiet
gcloud services enable iam.googleapis.com --quiet
echo "✅ APIs enabled"

echo ""
echo "👤 Step 2: Creating service account..."
# Check if service account already exists
if gcloud iam service-accounts describe $SA_EMAIL &>/dev/null; then
    echo "⚠️  Service account already exists, skipping creation"
else
    gcloud iam service-accounts create $SA_NAME \
      --display-name="GitHub Actions Service Account" \
      --project=$PROJECT_ID
    echo "✅ Service account created"
fi

echo ""
echo "🔐 Step 3: Granting permissions..."
# Grant Cloud Run Admin
gcloud projects add-iam-policy-binding $PROJECT_ID \
  --member="serviceAccount:${SA_EMAIL}" \
  --role="roles/run.admin" \
  --condition=None \
  --quiet

# Grant Service Account User
gcloud projects add-iam-policy-binding $PROJECT_ID \
  --member="serviceAccount:${SA_EMAIL}" \
  --role="roles/iam.serviceAccountUser" \
  --condition=None \
  --quiet

# Grant Storage Admin
gcloud projects add-iam-policy-binding $PROJECT_ID \
  --member="serviceAccount:${SA_EMAIL}" \
  --role="roles/storage.admin" \
  --condition=None \
  --quiet

# Grant Cloud Build Service Account permissions
PROJECT_NUMBER=$(gcloud projects describe $PROJECT_ID --format="value(projectNumber)")
gcloud projects add-iam-policy-binding $PROJECT_ID \
  --member="serviceAccount:${PROJECT_NUMBER}@cloudbuild.gserviceaccount.com" \
  --role="roles/run.admin" \
  --condition=None \
  --quiet

echo "✅ Permissions granted"

echo ""
echo "🔑 Step 4: Creating service account key..."
KEY_FILE="github-actions-key-${PROJECT_ID}.json"
gcloud iam service-accounts keys create $KEY_FILE \
  --iam-account=$SA_EMAIL

echo "✅ Key created: $KEY_FILE"

echo ""
echo "🔐 Step 5: Generating SECRET_KEY..."
SECRET_KEY=$(openssl rand -hex 32)

echo ""
echo "===================================="
echo "✅ Setup Complete!"
echo "===================================="
echo ""
echo "📝 Add these secrets to your GitHub repository:"
echo ""
echo "1. Go to: https://github.com/YOUR_USERNAME/YOUR_REPO/settings/secrets/actions"
echo "2. Click 'New repository secret' and add:"
